Page._content:
/* Custom comments component styles */

#custom-comments {
  margin-top: 2rem;
  padding: 1rem;
  background: var(--card-bg);
  border-radius: var(--card-border-radius);
  box-shadow: var(--card-box-shadow);
}

.comments-wrapper {
  display: flex;
  flex-direction: column;
  gap: 2rem;
}

/* Comments List */
.comments-list {
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
}

.no-comments {
  text-align: center;
  color: var(--text-light);
  padding: 2rem 0;
  font-style: italic;
}

.comment-item {
  display: flex;
  gap: 1rem;
  padding: 1rem;
  background: var(--card-hover-bg);
  border-radius: var(--card-border-radius);
  transition: all 0.3s ease;
}

.comment-item:hover {
  box-shadow: var(--card-hover-box-shadow);
  transform: translateY(-2px);
}

.comment-avatar {
  flex-shrink: 0;
  width: 50px;
  height: 50px;
}

.comment-avatar img {
  width: 100%;
  height: 100%;
  border-radius: 50%;
  object-fit: cover;
  border: 2px solid var(--border-color);
}

.comment-content {
  flex: 1;
}

.comment-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 0.5rem;
}

.comment-username {
  font-weight: bold;
  color: var(--text-color);
  font-size: 0.9rem;
}

.comment-date {
  font-size: 0.75rem;
  color: var(--text-light);
}

.comment-body {
  color: var(--text-color);
  line-height: 1.6;
  font-size: 0.9rem;
}

/* Comment Form */
.comment-form-container {
  padding: 1rem;
  background: var(--card-hover-bg);
  border-radius: var(--card-border-radius);
}

.comment-form-title {
  margin-bottom: 1rem;
  font-size: 1.2rem;
  color: var(--text-color);
  font-weight: bold;
}

.login-prompt {
  text-align: center;
  color: var(--text-light);
  padding: 1rem 0;
  font-style: italic;
}

.comment-form {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.form-group {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}

.comment-textarea {
  width: 100%;
  min-height: 120px;
  padding: 1rem;
  border: 1px solid var(--border-color);
  border-radius: var(--card-border-radius);
  background: var(--input-bg);
  color: var(--text-color);
  font-size: 0.9rem;
  line-height: 1.6;
  resize: vertical;
